document.addEventListener("DOMContentLoaded",()=>{let e=document.querySelector("select"),t=document.querySelector("body"),l=document.querySelectorAll("article");e.addEventListener("change",e=>{(()=>{t.classList.remove("green","yellow","red","blue");for(let e of l)e.classList.remove("display")})(),t.classList.add(e.target.value),document.querySelector("#"+e.target.value).classList.add("display")})});